import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class Recipe {
  final String name;
  final String description;
  final String imageUrl;

  Recipe({
    required this.name,
    required this.description,
    required this.imageUrl,
  });
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      home: Scaffold(
        appBar: AppBar(
          title: Text('Sobremesas'),
        ),
        body: RecipeList(),
      ),
    );
  }
}

class RecipeList extends StatelessWidget {
  final List<Recipe> recipes = [
    Recipe(
      name: 'Brownie de Chocolate',
      description: 'Um delicioso brownie de chocolate.',
      imageUrl: 'data:image/jpeg;base64,',
    ),
    Recipe(
      name: 'Sorvete de Baunilha',
      description: 'Sorvete cremoso de baunilha.',
      imageUrl: 'https://www.mundoboaforma.com.br/wp-content/uploads/2022/07/Sorvete-vegano-de-baunilha.jpg',
    ),
    Recipe(
      name: 'Mousse de Morango',
      description: 'Mousse leve e refrescante de morango.',
      imageUrl: 'https://p2.trrsf.com/image/fget/cf/1200/900/middle/images.terra.com/2023/07/29/1315551139-mousse-de-morango.jpg',
    ),
  ];

  @override
  Widget build(BuildContext context) {
    return ListView(
      children: recipes.map((recipe) {
        return Card(
          margin: EdgeInsets.all(16.0),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: <Widget>[
              Image.network(
                recipe.imageUrl,
                fit: BoxFit.cover,
                height: 150,
              ),
              Padding(
                padding: EdgeInsets.all(16.0),
                child: Column(
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: <Widget>[
                    Text(
                      recipe.name,
                      style: TextStyle(
                        fontSize: 20,
                        fontWeight: FontWeight.bold,
                      ),
                    ),
                    SizedBox(height: 8),
                    Text(
                      recipe.description,
                      style: TextStyle(
                        fontSize: 16,
                      ),
                    ),
                  ],
                ),
              ),
            ],
          ),
        );
      }).toList(),
    );
  }
}
